import { useNavigate } from "react-router-dom";

// track golfer shown
import React, { useState } from "react";

//hardcoded golfer data
const golfers = [
  {
    id: 1,
    name: "Tiger Woods",
    handicap: 2,
    homeCourse: "Northview Golf Club",
    distance: "Weekdays",
    photo: "https://source.unsplash.com/400x600/?golf"
  },
  {
    id: 2,
    name: "Phil Mickelson",
    handicap: 4,
    homeCourse: "Surrey Golf Club",
    distance: "Weekends",
    photo: "https://source.unsplash.com/400x600/?golfer"
  },
  {
    id: 3,
    name: "Scottie Scheffler",
    handicap: 1,
    homeCourse: "Morgan Creek Golf Course",
    distance: "Weekdays",
    photo: "https://source.unsplash.com/400x600/?golf-course"
  }
];


// main app component
export default function Match() {
  const navigate = useNavigate();

// tracks currently selected golfer
  const [index, setIndex] = useState(0);
  const current = golfers[index]; 

// User clicks yes, logs golfer name, moves to next golfer
  const handleYes = () => {
  navigate("/chat", { state: { user: current } });
};

// User clicks no, logs golfer name, moves to next golfer
  const handleNo = () => {
      console.log("No to:", current.name);
      nextCard();
    };

// Function to move to next golfer card
  const nextCard = () => {
    setIndex((prev) => (prev + 1 < golfers.length ? prev + 1 : 0));
  };

// jsx returned here builds the UI
  return (
      <main className="min-h-screen bg-bunker flex items-center justify-center p-4">
  {current ? (
    <div className="bg-white p-6 rounded-2xl shadow-md max-w-sm w-full text-center border-t-8 border-[#3B7A57]">
      {/* Avatar placeholder */}
      <div className="w-20 h-20 bg-[#3B7A57] text-white text-2xl flex items-center justify-center rounded-full mx-auto mb-4">
        {current.name.charAt(0)}
      </div>

      {/* User info */}
      <h2 className="text-2xl font-bold text-[#2C2C2C] mb-1">{current.name}</h2>
      <p className="text-sm text-gray-600 mb-1">Handicap: {current.handicap}</p>
      <p className="text-sm text-gray-600 mb-1">Home Course: {current.homeCourse}</p>
      <p className="text-sm text-gray-600 mb-4">Distance: {current.availability}</p>

      {/* Buttons */}
      <div className="flex justify-between gap-4">
        <button
          onClick={handleNo}
          className="flex-1 bg-red-500 hover:bg-red-600 text-white py-2 rounded-full shadow"
        >
          ‚è≠Ô∏è Skip
        </button>
        <button
          onClick={handleYes}
          className="flex-1 bg-[#3B7A57] hover:bg-green-800 text-white py-2 rounded-full shadow"
        >
          üí¨ Chat
        </button>
      </div>
    </div>
  ) : (
    <p className="text-gray-500">No more golfers!</p>
  )}
</main>
    );
}
